import { Component } from '@angular/core';
import '@vaadin/button';
import '@vaadin/grid';
import '@vaadin/text-field';
import { UntypedFormGroup, UntypedFormControl } from '@angular/forms';

class Person {
  constructor(public firstName: string, public lastName: string) {}
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  people: Person[] = [];
  form = new UntypedFormGroup({
    firstName: new UntypedFormControl(''),
    lastName: new UntypedFormControl('')
  });

  addPerson() {
    this.people = [
      ...this.people,
      new Person(this.form.value.firstName, this.form.value.lastName)
    ];
    this.form.reset();
  }
}
<form [formGroup]="form" (ngSubmit)="addPerson()">
  <vaadin-text-field
    label="First Name"
    formControlName="firstName"
    ngDefaultControl
  >
  </vaadin-text-field>
  <vaadin-text-field
    label="Last Name"
    formControlName="lastName"
    ngDefaultControl
  >
  </vaadin-text-field>
  <vaadin-button (click)="addPerson()"> Add </vaadin-button>
</form>

<vaadin-grid [items]="people">
  <vaadin-grid-column path="firstName" header="First name">
  </vaadin-grid-column>
  <vaadin-grid-column path="lastName" header="Last name"> </vaadin-grid-column>
</vaadin-grid>
